import { useState } from 'react'
import ExpenseItem from './ExpenseItem'
import './ExpenseList.css'
import Card from '../UI/Card'
import ExpensesFilter from '../ExpensesFilter/ExpensesFilter'

function ExpenseList(props) {
  const [pickedYear, setPickedYear] = useState('2020')
  const yearPickedHandler = (selectedYear) => {
    setPickedYear(selectedYear)
  }

  const filteredExpenses = props.item.filter((expense) => {
    return expense.date.getFullYear().toString() === pickedYear
  })

  let filteredContent = <p>No expenses found!!</p>;

  if (filteredExpenses.length > 0) {
    filteredContent = filteredExpenses.map(expense => (
        <ExpenseItem
          key={expense.id}
          Title={expense.title}
          Amount={expense.amount}
          Date={expense.date}
        />
    ))
  }

  return (
    <Card className="expenses">
      <ExpensesFilter select={pickedYear} yearPicked={yearPickedHandler} />
      {filteredContent}
      {/* <ExpenseItem
          Title={props.expenses[0].title}
          Amount={props.expenses[0].amount}
          Date={props.expenses[0].date}
        />

        <ExpenseItem
          Title={props.expenses[1].title}
          Amount={props.expenses[1].amount}
          Date={props.expenses[1].date}
        />

        <ExpenseItem
          Title={props.expenses[2].title}
          Amount={props.expenses[2].amount}
          Date={props.expenses[2].date}
        />

        <ExpenseItem
          Title={props.expenses[3].title}
          Amount={props.expenses[3].amount}
          Date={props.expenses[3].date}
        /> */}
      {/* map(function (expense) {
        return 
      }) */}
    </Card>
  )
}

export default ExpenseList
