import { useState } from 'react'
import ExpenseList from './components/Expenses/ExpenseList'
import NewExpense from './components/NewExpense/NewExpense'

const expenses = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
]

function App() {

  const [addNewExpense, setAddNewExpense] = useState(expenses)

  const onAddExpenseDataHandler = (expenseData) => { // getting the hold of expenseData from the newExpense component using child parent relation
    setAddNewExpense(preValue=> {
      return [expenseData, ...preValue]
    })
  }


  return (
    <div>
      <NewExpense onAddExpenseData= {onAddExpenseDataHandler}/>
      <ExpenseList item= {expenses}/>
    </div>
  )
}

export default App
